mapper.update:
update tbl_user
set user_password = #{userPassword}
where user_id = #{userId}

update tbl_user 
set user_nickname = #{userNickname}, user_phone = #{userPhone}, user_email = #{userEmail} 
where user_id = #{userId}

update tbl_corp
set corp_address = #{corpAddress}, corp_register_number = #{corpRegisterNumber}, corp_introduction_text = #{corpIntroductionText}, file_system_name = #{fileSystemName} 
where user_id = #{userId}
